const body=document.querySelector("body"),buttons=document.querySelectorAll(".btn"),closebuttons=document.querySelectorAll(".closePopup"),calcPrice=document.querySelector(".calcPrice"),res=document.querySelector(".resCalc"),burger=document.querySelector(".burger"),nav=document.querySelector("nav");var priceForOnePiece;function calculatePrice(e,r){if("fullproject"==e)priceForOnePiece=500;else if("withoutGreenPlan"==e||"greenPlan"==e)priceForOnePiece=400;else{if("flowerGarden"==e)return(priceForOnePiece=250)*Number(r);if("eskiz"==e)return 1e3}return priceForOnePiece*Number(r)/100}function showPrice(e){res.parentNode.style.display="inline-block",res.innerHTML=`${e}`}function showNavigation(){nav.classList.toggle("show-hide"),0==nav.classList.length?burger.querySelector("img").setAttribute("src","icons/list.svg"):burger.querySelector("img").setAttribute("src","icons/x-lg.svg")}for(btn of buttons)btn.addEventListener("click",(e=>{popupClass=e.target.classList[1],popupNode=document.querySelector(`.${popupClass}.popup`),popupNode.style.display="inline-block",body.style.overflow="hidden"}));for(btn of closebuttons)btn.addEventListener("click",(e=>{e.target.parentNode.parentNode.style.display="none",body.style.overflow="visible"}));calcPrice.addEventListener("click",(()=>{const e=document.querySelector(".checks input[type='radio']:checked").id,r=document.querySelector("#area").value;price=calculatePrice(e,r),showPrice(price)})),burger.addEventListener("click",showNavigation);